package day1;

public class 변수 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age;	//age변수 선언
		age = 49;	//변수에 값 넣음
		float weight = 70.44f;	//weight변수 선언 및 변수에 값 넣음
		
		System.out.println(age);	//변수 값 출력
		System.out.println(weight);
		
		age = 50;
		weight = weight+3;
		System.out.println(age);
		System.out.println(weight);

	}

}
