package day2;

public class 반복문_for_예제2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
	// 문제 )) 1~100까지의 합을 for 문을 이용하여 작성해라
		
		int sum = 0;
		
		for(int i=1; i<=100; i++) {
			
		sum = sum+i;
			
		}
		   
		System.out.println(" 1부터 100까지의 합은 = " + sum);
	
	
	
	}

}
